import axios from 'axios';

const apiUrl = "http://localhost:5107"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async (name) => {
    console.log('addTask', name);

    try {
      const response = await axios.post(`${apiUrl}/items`, {
        Name: name,
        IsComplete: false, // Assuming you want to set IsComplete to false for new tasks
      });

      return response.data;
    } catch (error) {
      // Handle error
      console.error('Error adding task:', error.message);
      return null;
    }
  },
  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });

    const response = await fetch(`${apiUrl}/items/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        IsComplete: isComplete,
      }),
    });

    if (response.ok) {
      return await response.json();
    } else {
      // Handle error
      console.error('Error setting task completion:', response.statusText);
      return null;
    }
  },
  deleteTask: async (id) => {
    console.log('deleteTask');

    const response = await fetch(`${apiUrl}/items/${id}`, {
      method: 'DELETE',
    });
    if (response.ok) {
      console.log('Task deleted successfully');
    } else {
      // Handle error
      console.error('Error deleting task:', response.statusText);
    }
  },
};
