import axios from 'axios';

const apiUrl = "http://localhost:5107"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async (name) => {
    console.log('addTask', name);

    const response = await fetch(`${apiUrl}/items`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        Name: name,
        IsComplete: false, // Assuming you want to set IsComplete to false for new tasks
      }),
    });

    if (response.ok) {
      return await response.json();
    } else {
      // Handle error
      console.error('Error adding task:', response.statusText);
      return null;
    }
  },

  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });

    const response = await fetch(`${apiBaseUrl}/items/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        IsComplete: isComplete,
      }),
    });

    if (response.ok) {
      return await response.json();
    } else {
      // Handle error
      console.error('Error setting task completion:', response.statusText);
      return null;
    }
  },
  deleteTask:async()=>{
