import axios from 'axios';
import config from './config';
const apiUrl = config.apiUrl;

const responseInterceptor = axios.interceptors.response.use(
  (response) => {
    dispatch(stopLoader());
    return response;
  },
  (error: any) => {
    dispatch(stopLoader());
    if (error.response.status == 500) {
      setError(true);
    }
    if (error.response.status == 401) {
      alert("you need to relogin");
    }
    if (error.response.status == 403) {
      alert("you are not alowed to do the action!");
    }
    return Promise.reject(error);
  }
);

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async (name) => {
    console.log('addTask', name);

    try {
      const response = await axios.post(`${apiUrl}/items`, {
        Name: 3,
        IsComplete: false, // Assuming you want to set IsComplete to false for new tasks
      });

      return response.data;
    } catch (error) {
      // Handle error
      console.error('Error adding task:', error.message);
      return null;
    }
  },
  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });

    try {
      const response = await axios.put(`${apiUrl}/items/${id}`, {
        IsComplete: isComplete,
      });

      return response.data;
    } catch (error) {
      // Handle error
      console.error('Error setting task completion:', error.message);
      return null;
    }
  },

  deleteTask: async (id) => {
    console.log('deleteTask');

    try {
      const response = await axios.delete(`${apiUrl}/items/${id}`);

      if (response.status === 200) {
        console.log('Task deleted successfully');
      } else {
        // Handle unexpected response status
        console.error('Error deleting task:', response.statusText);
      }
    } catch (error) {
      // Handle error
      console.error('Error deleting task:', error.message);
    }
  },
};
